/**
 * RunAnalytics Search
 * Client-side search powered by Fuse.js
 * Supports fuzzy matching, multilingual content, and keyboard navigation
 */

(function() {
  'use strict';

  // State
  let searchIndex = null;
  let fuse = null;
  let currentLanguage = document.documentElement.lang || 'en';
  let isLoading = false;
  let selectedResultIndex = -1;

  // DOM Elements
  const searchInput = document.getElementById('site-search');
  const searchResults = document.getElementById('search-results');
  const searchContainer = document.querySelector('.search-container');

  if (!searchInput || !searchResults) return;

  // Fuse.js configuration - optimized for technical content
  const fuseOptions = {
    keys: [
      { name: 'title', weight: 3 },        // Title most important
      { name: 'keywords', weight: 2 },     // Keywords second
      { name: 'description', weight: 1.5 }, // Description third
      { name: 'content', weight: 1 }       // Content last
    ],
    threshold: 0.3,           // Balanced precision/tolerance
    distance: 100,            // How far to search
    minMatchCharLength: 2,    // Minimum chars to match
    includeScore: true,       // For relevance sorting
    includeMatches: true,     // For highlighting
    ignoreLocation: true,     // Search anywhere in text
    useExtendedSearch: false
  };

  // UI Strings by language
  const uiStrings = {
    ca: {
      placeholder: 'Cerca...',
      noResults: 'No s\'han trobat resultats per',
      loading: 'Cercant...',
      resultsCount: '{count} resultat(s)',
      pressEnter: 'Prem Enter per visitar',
      pressEsc: 'Prem Esc per tancar'
    },
    en: {
      placeholder: 'Search...',
      noResults: 'No results found for',
      loading: 'Searching...',
      resultsCount: '{count} result(s)',
      pressEnter: 'Press Enter to visit',
      pressEsc: 'Press Esc to close'
    },
    es: {
      placeholder: 'Buscar...',
      noResults: 'No se encontraron resultados para',
      loading: 'Buscando...',
      resultsCount: '{count} resultado(s)',
      pressEnter: 'Presione Enter para visitar',
      pressEsc: 'Presione Esc para cerrar'
    },
    fr: {
      placeholder: 'Rechercher...',
      noResults: 'Aucun résultat trouvé pour',
      loading: 'Recherche...',
      resultsCount: '{count} résultat(s)',
      pressEnter: 'Appuyez sur Entrée pour visiter',
      pressEsc: 'Appuyez sur Échap pour fermer'
    },
    de: {
      placeholder: 'Suchen...',
      noResults: 'Keine Ergebnisse gefunden für',
      loading: 'Suchen...',
      resultsCount: '{count} Ergebnis(se)',
      pressEnter: 'Drücken Sie Enter zum Besuchen',
      pressEsc: 'Drücken Sie Esc zum Schließen'
    },
    it: {
      placeholder: 'Cerca...',
      noResults: 'Nessun risultato trovato per',
      loading: 'Ricerca...',
      resultsCount: '{count} risultato(i)',
      pressEnter: 'Premi Invio per visitare',
      pressEsc: 'Premi Esc per chiudere'
    },
    pt: {
      placeholder: 'Pesquisar...',
      noResults: 'Nenhum resultado encontrado para',
      loading: 'Pesquisando...',
      resultsCount: '{count} resultado(s)',
      pressEnter: 'Pressione Enter para visitar',
      pressEsc: 'Pressione Esc para fechar'
    }
  };

  const ui = uiStrings[currentLanguage] || uiStrings.en;

  /**
   * Load search index lazily
   */
  async function loadSearchIndex() {
    if (searchIndex) return searchIndex;
    if (isLoading) return null;

    isLoading = true;

    try {
      const response = await fetch('/assets/search-index.json');
      if (!response.ok) throw new Error('Failed to load search index');

      const data = await response.json();
      searchIndex = data;

      // Initialize Fuse with current language data
      if (searchIndex[currentLanguage]) {
        fuse = new Fuse(searchIndex[currentLanguage], fuseOptions);
      }

      isLoading = false;
      return searchIndex;
    } catch (error) {
      console.error('Error loading search index:', error);
      isLoading = false;
      return null;
    }
  }

  /**
   * Perform search
   */
  function performSearch(query) {
    if (!query || query.length < 2) {
      hideResults();
      return;
    }

    if (!fuse) {
      showLoading();
      loadSearchIndex().then(() => {
        if (fuse) performSearch(query);
        else hideResults();
      });
      return;
    }

    const results = fuse.search(query);
    displayResults(results, query);
  }

  /**
   * Display search results
   */
  function displayResults(results, query) {
    selectedResultIndex = -1;

    if (results.length === 0) {
      searchResults.innerHTML = `
        <div class="search-no-results">
          <p>${ui.noResults} <strong>"${escapeHtml(query)}"</strong></p>
        </div>
      `;
      showResults();
      return;
    }

    const resultsHtml = results.slice(0, 8).map((result, index) => {
      const item = result.item;
      const score = (1 - result.score).toFixed(2);

      // Highlight matching text
      const highlightedTitle = highlightMatches(item.title, result.matches, 'title');
      const highlightedDesc = highlightMatches(
        item.description || item.content,
        result.matches,
        'description'
      );

      return `
        <a href="${item.url}"
           class="search-result-item"
           data-index="${index}"
           role="option"
           aria-selected="false">
          <div class="search-result-title">${highlightedTitle}</div>
          <div class="search-result-description">${truncate(highlightedDesc, 120)}</div>
          <div class="search-result-meta">
            <span class="search-result-url">${item.url}</span>
          </div>
        </a>
      `;
    }).join('');

    searchResults.innerHTML = `
      <div class="search-results-header">
        <span>${ui.resultsCount.replace('{count}', results.length)}</span>
      </div>
      <div class="search-results-list" role="listbox">
        ${resultsHtml}
      </div>
      <div class="search-results-footer">
        <kbd>↑↓</kbd> ${ui.pressEnter} · <kbd>Esc</kbd> ${ui.pressEsc}
      </div>
    `;

    showResults();
  }

  /**
   * Highlight matched text
   */
  function highlightMatches(text, matches, key) {
    if (!matches || !text) return escapeHtml(text);

    const relevantMatches = matches.filter(m => m.key === key);
    if (relevantMatches.length === 0) return escapeHtml(text);

    let highlighted = text;
    const match = relevantMatches[0];

    if (match.indices && match.indices.length > 0) {
      // Sort indices in reverse to avoid index shifting
      const sortedIndices = match.indices.sort((a, b) => b[0] - a[0]);

      sortedIndices.forEach(([start, end]) => {
        const before = highlighted.slice(0, start);
        const matched = highlighted.slice(start, end + 1);
        const after = highlighted.slice(end + 1);
        highlighted = before + '<mark>' + escapeHtml(matched) + '</mark>' + after;
      });

      return highlighted;
    }

    return escapeHtml(text);
  }

  /**
   * Show loading state
   */
  function showLoading() {
    searchResults.innerHTML = `
      <div class="search-loading">
        <p>${ui.loading}</p>
      </div>
    `;
    showResults();
  }

  /**
   * Show results container
   */
  function showResults() {
    searchResults.classList.remove('hidden');
    searchResults.classList.add('visible');
    searchInput.setAttribute('aria-expanded', 'true');
  }

  /**
   * Hide results container
   */
  function hideResults() {
    searchResults.classList.remove('visible');
    searchResults.classList.add('hidden');
    searchInput.setAttribute('aria-expanded', 'false');
    selectedResultIndex = -1;
  }

  /**
   * Navigate results with keyboard
   */
  function navigateResults(direction) {
    const resultItems = searchResults.querySelectorAll('.search-result-item');
    if (resultItems.length === 0) return;

    // Remove previous selection
    if (selectedResultIndex >= 0 && selectedResultIndex < resultItems.length) {
      resultItems[selectedResultIndex].setAttribute('aria-selected', 'false');
      resultItems[selectedResultIndex].classList.remove('selected');
    }

    // Calculate new index
    if (direction === 'down') {
      selectedResultIndex = Math.min(selectedResultIndex + 1, resultItems.length - 1);
    } else if (direction === 'up') {
      selectedResultIndex = Math.max(selectedResultIndex - 1, 0);
    }

    // Apply new selection
    if (selectedResultIndex >= 0 && selectedResultIndex < resultItems.length) {
      resultItems[selectedResultIndex].setAttribute('aria-selected', 'true');
      resultItems[selectedResultIndex].classList.add('selected');
      resultItems[selectedResultIndex].scrollIntoView({ block: 'nearest' });
    }
  }

  /**
   * Handle Enter key - navigate to selected result
   */
  function selectCurrentResult() {
    const resultItems = searchResults.querySelectorAll('.search-result-item');
    if (selectedResultIndex >= 0 && selectedResultIndex < resultItems.length) {
      window.location.href = resultItems[selectedResultIndex].href;
    }
  }

  /**
   * Utility: Escape HTML
   */
  function escapeHtml(text) {
    const div = document.createElement('div');
    div.textContent = text;
    return div.innerHTML;
  }

  /**
   * Utility: Truncate text
   */
  function truncate(text, maxLength) {
    if (!text) return '';
    const stripped = text.replace(/<[^>]*>/g, ''); // Remove HTML tags
    if (stripped.length <= maxLength) return text;
    return text.slice(0, maxLength) + '...';
  }

  /**
   * Debounce function
   */
  function debounce(func, wait) {
    let timeout;
    return function executedFunction(...args) {
      const later = () => {
        clearTimeout(timeout);
        func(...args);
      };
      clearTimeout(timeout);
      timeout = setTimeout(later, wait);
    };
  }

  // Event Listeners

  // Search input
  searchInput.addEventListener('input', debounce(function(e) {
    performSearch(e.target.value.trim());
  }, 300));

  // Keyboard navigation
  searchInput.addEventListener('keydown', function(e) {
    if (searchResults.classList.contains('hidden')) return;

    switch(e.key) {
      case 'ArrowDown':
        e.preventDefault();
        navigateResults('down');
        break;
      case 'ArrowUp':
        e.preventDefault();
        navigateResults('up');
        break;
      case 'Enter':
        e.preventDefault();
        selectCurrentResult();
        break;
      case 'Escape':
        e.preventDefault();
        hideResults();
        searchInput.blur();
        break;
    }
  });

  // Click outside to close
  document.addEventListener('click', function(e) {
    if (!searchContainer.contains(e.target)) {
      hideResults();
    }
  });

  // Focus input to show recent results (if any)
  searchInput.addEventListener('focus', function() {
    if (searchInput.value.trim().length >= 2) {
      performSearch(searchInput.value.trim());
    }
  });

  // Set placeholder
  searchInput.setAttribute('placeholder', ui.placeholder);
  searchInput.setAttribute('aria-label', ui.placeholder);

  // Preload search index on input focus (lazy load)
  searchInput.addEventListener('focus', function() {
    if (!searchIndex) {
      loadSearchIndex();
    }
  }, { once: true });

})();
